import React, {useState} from 'react'
import { StatusBar } from 'expo-status-bar'
import { StyleSheet, Text, View } from 'react-native'
import { Data } from './src/Data'
import { Switchers } from './src/Switchers'
import { BIO } from './src/BIO'
import { library } from '@fortawesome/fontawesome-svg-core'
import { fab } from '@fortawesome/free-brands-svg-icons'
import {
  faCloud, faWater, faThermometerHalf, faFire, faLightbulb, faDoorOpen
} from "@fortawesome/free-solid-svg-icons";
import * as LocalAuthentication from 'expo-local-authentication'

library.add(fab, faCloud, faWater, faThermometerHalf, faFire, faLightbulb, faDoorOpen)
export default function App() {
  return (
    <View style={styles.container}>

      <BIO/>
      <View style={{ justifyContent: 'center',}}><Text style={styles.baseText}>ESP32 HOUSE CONTROL</Text></View> 
      <View style={styles.bottom} />
      <Data/>
      <View style={styles.bottom} />
      <Switchers/>  

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    backgroundColor: '#F5F0F0',
    alignItems: 'center',
    justifyContent: 'space-around', 
    height: '15%', 
  },
  baseText: {
    fontSize: 25,
    fontWeight: 'bold',
  },
  bottom: {
    flex: 1,
    flexDirection: 'row',
    borderColor: 'black',
    borderStartWidth: 500,
    height: '0.2%',
  },
});
