import React, {useState} from 'react'
import {NavigationContainer} from '@react-navigation/native';
import { StyleSheet, Text, View } from 'react-native'
import { Data } from './Sensors'
import { Switchers } from './Switchers'
import { Start } from './Start'

export default function App() {
  const [isLogin, setLogin] = useState(false);
  const checkLogin = () => {
    setLogin(true)
  }
    return(
    <NavigationContainer>
      <View style={styles.container}>   
        <View style={{height: '15%', justifyContent: 'center',}}><Text style={styles.baseText}>ESP32 HOUSE CONTROL</Text></View> 
        <View style={styles.bottom} />
        <Data/>
        <View style={styles.bottom} />
        <Switchers/>    
        <View style={{height: '10%', justifyContent: 'center',}}><Text style={styles.baseText}>LOGIN MENU</Text></View> 
      </View>
    </NavigationContainer>
    );
  }

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    backgroundColor: '#F5F0F0',
    alignItems: 'center',
    justifyContent: 'space-around',     
  },
  baseText: {
    fontSize: 25,
    fontWeight: 'bold',
  },
  bottom: {
    flex: 1,
    flexDirection: 'row',
    borderColor: 'black',
    borderStartWidth: 280,
    height: '0.2%',
  },
});
