import React, { useState } from "react";
import { BIO } from './BIO'
import {
  StyleSheet,
  Text,
  View,
  Image,
  TextInput,
  Button,
  TouchableOpacity,
} from "react-native";
import { StatusBar } from 'expo-status-bar'
import * as LocalAuthentication from 'expo-local-authentication'
import { clickProps } from "react-native-web/dist/cjs/modules/forwardedProps";

export const Start = (props) => {
    const [password, setPassword] = useState("");  
    return (
      <View style={styles.container}>
        <View style={{height: '15%', justifyContent: 'center',}}>
          <Text style={styles.baseText}>WELCOME HOME</Text>
          <Text style={styles.baseText}>PLEASE, AUTORIZE</Text>
        </View> 
        <BIO/> 
        <StatusBar style="auto" />
        <View style={styles.inputView}>
          <TextInput
            style={styles.TextInput}
            placeholder="Password."
            placeholderTextColor="#003f5c"
            secureTextEntry={true}
            onChangeText={(password) => setPassword(password)}
          /> 
        </View> 
        {/* <TouchableOpacity>
          <Text style={styles.forgot_button}>Forgot Password?</Text> 
        </TouchableOpacity>  */}
        <TouchableOpacity onPress={props.onPress} style={styles.loginBtn}>
        <Button style={styles.loginText} title="Login with password" color='#a66f6f' />
        </TouchableOpacity> 
      </View> 
    );
  }
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: "#fff",
      alignItems: "center",
      justifyContent: "center",
    },
    image: {
      marginBottom: 40,
    },
    inputView: {
      backgroundColor: "#FFC0CB",
      borderRadius: 5,
      width: "70%",
      height: 45,
      marginBottom: 20,
      alignItems: "center",
    },
    TextInput: {
      height: 50,
      flex: 1,
      padding: 10,
      marginLeft: 20,
    },
    forgot_button: {
      height: 30,
      marginBottom: 30,
    },
    loginBtn: {
      width: "50%",
      borderRadius: 5,
      //height: 50,
      alignItems: "center",
      justifyContent: "center",
      marginTop: 40,
      backgroundColor: "#a66f6f",
      color: "white",
    },
});
