import React, {useState} from 'react'
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Switch } from 'react-native';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'

export const Switchers = () => {
  const [isEnabled1, setIsEnabled1] = useState(false);
  const [isEnabled2, setIsEnabled2] = useState(false);
  const [isEnabled3, setIsEnabled3] = useState(false);

  const toggleSwitch1 = () => setIsEnabled1(previousState1 => !previousState1);
  const toggleSwitch2 = () => setIsEnabled2(previousState2 => !previousState2);
  const toggleSwitch3 = () => setIsEnabled3(previousState3 => !previousState3);
  return (
    <View style={styles.container}>
      <View style={styles.mini}>
      <FontAwesomeIcon style={{color: 'red'}} icon="fa-solid fa-fire" />
      <Text style={[styles.baseText, {color: 'red'}]}>  Fire in the house!  </Text>
      <Switch
        trackColor={{false: '#767577', true: 'red'}}
        thumbColor={isEnabled1 ? 'red' : '#f4f3f4'}
        ios_backgroundColor="#3e3e3e"
        onValueChange={toggleSwitch1}
        value={isEnabled1}
      />
      </View>

      <View style={styles.mini}>
      <FontAwesomeIcon style={styles.awesome} icon="fa-solid fa-lightbulb" />
      <Text style={styles.baseText}>  Lighting  </Text>
      <Switch
        trackColor={{false: '#767577', true: '#81b0ff'}}
        thumbColor={isEnabled2 ? '#f5dd4b' : '#f4f3f4'}
        ios_backgroundColor="#3e3e3e"
        onValueChange={toggleSwitch2}
        value={isEnabled2}
      />
      </View>

      <View style={styles.mini}>
      <FontAwesomeIcon style={styles.awesome} icon="fa-solid fa-door-open" />
      <Text style={styles.baseText}>  Door  </Text>
      <Switch 
        trackColor={{false: '#767577', true: '#81b0ff'}}
        thumbColor={isEnabled3 ? '#f5dd4b' : '#f4f3f4'}
        ios_backgroundColor="#3e3e3e"
        onValueChange={toggleSwitch3}
        value={isEnabled3}
      />
      </View>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: 'column',
    height: '42%',
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'space-around',
  },
  mini: {
    flexDirection: 'row',
  },
  baseText: {
    fontSize: 20,
  },
  awesome:{
    color: '#0066cc',   
    height: '1em', 
    fontSize: 30,  
  },
  
});
