import React, {useState} from 'react'
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'

export const Data = () => {
  return (
    <View style={styles.container}>
      <View style={styles.mini}>
      <FontAwesomeIcon icon="fa-solid fa-cloud" /> 
      <Text style={styles.baseText}>Air pollution: </Text>
      </View>

      <View style={styles.mini}>
      <FontAwesomeIcon icon="fa-solid fa-thermometer-half" />
      <Text style={styles.baseText}>Temperature: </Text>
      </View>

      <View style={styles.mini}>
      <FontAwesomeIcon icon="fa-solid fa-water" />
      <Text style={styles.baseText}>Humidity: </Text>
      </View>
    </View> 
  );
}

const styles = StyleSheet.create({
  container: {
    height: '42%',
    flexDirection: 'column',
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'space-around',
  },
  mini: {
    flexDirection: 'row',
    fontSize: 20,  
  },
  baseText: {
    fontSize: 20,    
  }
});
