import React, {useState} from 'react'
import { StatusBar } from 'expo-status-bar'
import { StyleSheet, Text, View } from 'react-native'
import { Data } from './src/Data'
import { Switchers } from './src/Switchers'
import { library } from '@fortawesome/fontawesome-svg-core'
import { fab } from '@fortawesome/free-brands-svg-icons'
import {
  faCloud, faWater, faThermometerHalf, faFire, faLightbulb, faDoorOpen
} from "@fortawesome/free-solid-svg-icons";

library.add(fab, faCloud, faWater, faThermometerHalf, faFire, faLightbulb, faDoorOpen)
export default function App() {
  return (
    <View style={styles.container}>
      <Text style={styles.baseText}>ESP32 HOUSE CONTROL</Text>
      <Data/>
      <View style={styles.bottom} />
      <Switchers/>   
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'space-around',
    height: '16%',
  },
  baseText: {
    fontSize: 23,
    fontWeight: 'bold',

  },
  bottom: {
    flex: 0.3,
    backgroundColor: 'pink',
    borderWidth: 5,
    borderBottomLeftRadius: 2,
    borderBottomRightRadius: 2,
  },
});
