import React, {useState} from 'react'
import {NavigationContainer} from '@react-navigation/native';
import { StyleSheet, Text, View } from 'react-native'
import { Data } from './src/Data'
import { Switchers } from './src/Switchers'
import { Start } from './src/Start'
import { library } from '@fortawesome/fontawesome-svg-core'
import { fab } from '@fortawesome/free-brands-svg-icons'
import {
  faCloud, faWater, faThermometerHalf, faFire, faLightbulb, faDoorOpen
} from "@fortawesome/free-solid-svg-icons";
import * as LocalAuthentication from 'expo-local-authentication'
import {createNativeStackNavigator} from '@react-navigation/native-stack';

library.add(fab, faCloud, faWater, faThermometerHalf, faFire, faLightbulb, faDoorOpen)

const Stack = createNativeStackNavigator();

export default function App() {
  // const [isLogin, setLogin] = useState(false);
  // const checkLogin = () => {
  //   setLogin(true)
  // }
    return(
      <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen 
          name="Home" 
          component={HomeScreen}
          options={{title: 'Control app'}}
        />
        <Stack.Screen 
          name="Profile" 
          component={ProfileScreen} 
        />
      </Stack.Navigator>
    </NavigationContainer>    
    ); 
}



const HomeScreen = ({navigation}) => {
  const goToData = () =>
    navigation.navigate('Profile', {data: 'data'});

  return (
    <View style={styles.container} >   
    <Start onPress={goToData}/>   
    </View>
  );
};

const ProfileScreen = ({navigation, route}) => {
  return (
    <View style={styles.container}> 
    {/* //<Text>This is page with {route.params.data}</Text>   */}
      <View style={{height: '15%', justifyContent: 'center',}}><Text style={styles.baseText}>ESP32 HOUSE CONTROL</Text></View> 
      <View style={styles.bottom} />
      <Data/>
      <View style={styles.bottom} />
      <Switchers/>    
      <View style={{height: '10%', justifyContent: 'center',}}><Text style={styles.baseText}>LOGIN MENU</Text></View> 
    </View>);
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    backgroundColor: '#F5F0F0',
    alignItems: 'center',
    justifyContent: 'space-around', 

  },
  baseText: {
    fontSize: 25,
    fontWeight: 'bold',
  },

});
