import React, { useState } from "react";
import { BIO } from './BIO'
import {
  SafeAreaView,
  StyleSheet,
  Text,
  View,
  Image,
  TextInput,
  Button,
  TouchableOpacity,
} from "react-native";
import { StatusBar } from 'expo-status-bar'
import * as LocalAuthentication from 'expo-local-authentication'
import { clickProps } from "react-native-web/dist/cjs/modules/forwardedProps";

export const Start = (props) => {
    const [password, setPassword] = useState("");  
    const [touch, setTouch] = useState(false);
    const [elementVisible, setElementVisible] = useState(false); 
    return (
      <View style={styles.container}>
        <View style={{height: '25%', justifyContent: 'center', alignItems: "center",}}>
          <Text style={styles.baseText}>WELCOME HOME</Text>
          <Text style={{fontSize: 20,}}>PLEASE, AUTORIZE</Text>
        </View> 
        <BIO/> 
        {/* <StatusBar style="auto" /> */}
        
        {/* <TouchableOpacity>
          <Text style={styles.forgot_button}>Forgot Password?</Text> 
        </TouchableOpacity>  */}
        
        
        {elementVisible ? (
          <View style={styles.inputView}>
            <TextInput
              style={styles.TextInput}
              placeholder="Password."
              placeholderTextColor="#003f5c"
              secureTextEntry={true}
              onChangeText={(password) => setPassword(password)}
            /> 
          </View>) : (<TouchableOpacity style={{height: "10%"}}>
          <Button 
            onPress={() => setElementVisible(!elementVisible)} 
            style={styles.loginBtn} 
            title="Login with password" 
            color='#a66f6f' />
        </TouchableOpacity>) }
         

        <TouchableOpacity style={{height: "10%"}}>
          <Button 
            onPress={props.onPress} 
            style={styles.loginBtn} 
            title="GO TO DATA" 
            color='#a66f6f' />
        </TouchableOpacity> 
      </View> 
    );
  }
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: "#fff",
      alignItems: "center",
      justifyContent: "center",
      width: '100%',
    },
    baseText: {
      fontSize: 30,
    },
    image: {
      marginBottom: 40,
    },
    inputView: {
      backgroundColor: "#FFC0CB",
      borderRadius: 5,
      //width: "70%",
      height: "4%",
      //marginBottom: 20,
      alignItems: "center",
    },
    TextInput: {
      height: "20%",
      flex: 1,
      // padding: 10,
      // marginLeft: 20,
    },
    forgot_button: {
      height: "5%",
      //marginBottom: 30,
    },
    loginBtn: {
      //width: "10%",
      borderRadius: 5,
      alignItems: "center",
      justifyContent: "center",
      //marginTop: 40,
      backgroundColor: "#a66f6f",
    },
});
