import React, {useState, useEffect} from 'react'
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import {useMutation, useQuery} from "@apollo/client";
import {GET_DATA} from "../query/sensor";

export const Sensors = () => {
  const {dataA, loadingA} = useQuery(GET_DATA)
//   const {dataT, loadingT} = useQuery(GET_DATA, {
//     variables: {
//         id: 2
//     }
//   })
//   const {dataH, loadingH} = useQuery(GET_DATA, {
//     variables: {
//         id: 3
//     }
// })

const {data, loading} = useQuery(GET_DATA, {
  variables: {
      id: 2
  }
})
let Air;
useEffect(() => {
if (!loading) {
  Air = data.getUser.Value
  console.log(Air)
}
}, [data])
// if (loadingA || loadingT || loadingH) {
//   return <h1>Loading...</h1>
// }
//console.log(dataA);
// console.log(dataT);
// console.log(dataH);
  return (
    <View style={styles.container}>
      <View style={styles.mini}>       
      <Text style={styles.baseText}>  Air pollution: </Text>
      <Text style={styles.baseText}>{Air}</Text>
      <Text style={styles.baseText}> ppm</Text>
      </View>

      <View style={styles.mini}>
      <Text style={styles.baseText}>  Temperature: </Text>
      <Text style={styles.baseText}>..</Text>
      <Text style={styles.baseText}> C</Text>
      </View>

      <View style={styles.mini}>
      <Text style={styles.baseText}>  Humidity: </Text>
      <Text style={styles.baseText}>..</Text>
      <Text style={styles.baseText}> %</Text>
      </View>
    </View> 
  );
}

const styles = StyleSheet.create({
  container: {
    height: '37.3%',
    flexDirection: 'column',
    backgroundColor: '#F5F0F0',
    alignItems: 'center',
    justifyContent: 'space-around',
  },
  mini: {
    flexDirection: 'row',
  },
  baseText: {
    fontSize: 20,    
  },
  awesome:{
    color: '#A07A7A',   
    // height: '1em', 
    fontSize: 30, 
  }
});
